/**
 * Submissions History Component (Standalone)
 * Displays all past code submissions and executions
 */

import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { CodeExecutionService } from '@core/services/code-execution.service';

interface Submission {
  id: string;
  language: string;
  code: string;
  output: string;
  error: string | null;
  executionTime: number;
  memory: number;
  status: string;
  createdAt: string;
}

@Component({
  selector: 'app-submissions-history',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="history-container">
      <div class="history-header">
        <h2>Submission History</h2>
        <button
          (click)="refreshSubmissions()"
          [disabled]="loading"
          class="refresh-btn"
        >
          {{ loading ? 'Loading...' : 'Refresh' }}
        </button>
      </div>

      <div
        *ngIf="loading"
        class="loading-state"
      >
        <p>Loading submissions...</p>
      </div>

      <div
        *ngIf="!loading && submissions.length === 0"
        class="empty-state"
      >
        <p>No submissions yet. Start coding!</p>
      </div>

      <div
        *ngIf="!loading && submissions.length > 0"
        class="submissions-list"
      >
        <div
          *ngFor="let submission of submissions; let i = index"
          class="submission-item"
        >
          <div class="submission-header">
            <div class="submission-info">
              <span
                class="language-badge"
                [ngClass]="'lang-' + submission.language"
              >
                {{ submission.language.toUpperCase() }}
              </span>
              <span
                class="status-badge"
                [ngClass]="'status-' + submission.status"
              >
                {{ submission.status === 'success' ? '‚úì Success' : '‚úó Error' }}
              </span>
              <span class="time-info">
                {{ formatDate(submission.createdAt) }}
              </span>
            </div>
            <button
              (click)="toggleSubmission(i)"
              class="expand-btn"
            >
              {{ expandedIndex === i ? '‚ñº' : '‚ñ∂' }}
            </button>
          </div>

          <div
            *ngIf="expandedIndex === i"
            class="submission-details"
          >
            <div class="code-section">
              <h4>Code:</h4>
              <pre class="code-block"><code>{{ submission.code }}</code></pre>
            </div>

            <div class="output-section">
              <h4>Output:</h4>
              <pre
                class="output-block"
                *ngIf="submission.status === 'success'"
                >{{ submission.output || '(No output)' }}</pre
              >
              <pre
                class="error-block"
                *ngIf="submission.status === 'error'"
                >{{ submission.error || submission.output }}</pre
              >
            </div>

            <div class="execution-stats">
              <span>‚è±Ô∏è Time: {{ submission.executionTime }}ms</span>
              <span>üíæ Memory: {{ submission.memory }}MB</span>
            </div>

            <button
              (click)="copyCode(submission.code)"
              class="copy-btn"
            >
              Copy Code
            </button>
          </div>
        </div>
      </div>

      <div
        *ngIf="error"
        class="error-message"
      >
        {{ error }}
        <button
          (click)="dismissError()"
          class="close-btn"
        >
          √ó
        </button>
      </div>
    </div>
  `,
  styles: [
    `
      .history-container {
        padding: 20px;
        background: #f9f9f9;
        border-radius: 8px;
        margin: 10px 0;
      }

      .history-header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        margin-bottom: 20px;
        border-bottom: 2px solid #ddd;
        padding-bottom: 10px;
      }

      .history-header h2 {
        margin: 0;
        color: #333;
        font-size: 1.5rem;
      }

      .refresh-btn {
        padding: 8px 16px;
        background: #007bff;
        color: white;
        border: none;
        border-radius: 4px;
        cursor: pointer;
        font-size: 0.9rem;
        transition: background 0.3s;
      }

      .refresh-btn:hover:not(:disabled) {
        background: #0056b3;
      }

      .refresh-btn:disabled {
        background: #ccc;
