/**
 * HTTP Interceptor Function for API requests
 * Modern standalone-compatible interceptor
 */

import { HttpInterceptorFn } from '@angular/common/http';
import { inject } from '@angular/core';
import { AuthService } from '../services/auth.service';

export const authInterceptor: HttpInterceptorFn = (req, next) => {
  const authService = inject(AuthService);
  const token = authService.getToken();

  if (token) {
    console.log('[Interceptor] Token value:', token);
    console.log('[Interceptor] Token type:', typeof token);
    console.log('[Interceptor] Token length:', token.length);
    console.log('[Interceptor] Adding token to request:', req.url);
    
    const authHeader = `Bearer ${token}`;
    console.log('[Interceptor] Authorization header:', authHeader.substring(0, 50) + '...');
    
    req = req.clone({
      setHeaders: {
        Authorization: authHeader,
      },
    });
  } else {
    console.log('[Interceptor] No token available for:', req.url);
  }

  return next(req);
};
